INSERT INTO `crime_legislation` (`id`, `article`, `legislation`, `description`) VALUES
	(1, '1.1', 'Infraction - Παράβαση', 'Παραβάσεις ή εγκλήματα τα οποία τιμωρούνται με χρηματικό πρόστιμο.'),
	(2, '1.2', 'Wobbler', 'Κάθε αδίκημα το οποίο αντιμετωπίζεται είτε ως πλημμέλημα είτε ως κακούργημα σύμφωνα με την απόφαση του εισαγγελέα.'),
	(3, '1.3', 'Misdemeanor - Πλημμέλημα', 'Εγκλήματα τα οποία τιμωρούνται με χρηματικό πρόστιμο και φυλάκιση έως 12 μήνες.'),
	(4, '1.4', 'Felony - Κακούργημα', 'Αξιόποινα εγκλήματα τα οποία τιμωρούνται με χρηματικό πρόστιμο και φυλάκιση άνω των 12 μηνών.'),
	(5, '1.5', 'Violent (Capital) Felony - H.U.T', 'Held Until Trial (Κρατείται μέχρι την δίκη). Η πολιτεία προβλέπει την φυλάκιση εφόρου ζωής ή/και την θανατική ποινή για τέτοια εγκλήματα.'),
	(6, '1.6', 'Principal ή Offender - Αυτουργός', 'Οποισδήποτε πράττει ένα έγκλημα ως αυτουργός.'),
	(7, '1.7', 'Accessory (before/after) the fact - Συνεργός', 'Οποιοσδήποτε ενθαρρύνει ή βοηθάει (έμεσα) στον προσχεδιασμό ενός εγκλήματος ή εν γνώση του συνεισφέρει στην αποφυγή ποινικής δίωξης μετά την επίτευξή της, αποκαλείται "συνεργός". Ένας συνεργός δεν παρίσταται στο έγκλημα αλλά γνωρίζει για την διάπραξή του (πχ ένα άτομο παραλαμβάνει ληστές σε διαφορετική τοποθεσία από αυτή της ληστείας).'),
	(8, '1.7', 'Accomplice - Συνένοχος', 'Οποιοσδήποτε συνεισφέρει ή βοηθάει άμεσα ή ενεργά στην διάπραξη εγκλήματος αποκαλείται "συνένοχος". Αυτός, είναι παρών στο έγκλημα και θα μπορούσε να καταδικαστεί για αυτό, ακόμη κι αν ο κύριος εγκληματίας δεν κατηγορηθεί ή καταδικαστεί (πχ ένα άτομο παραλαμβάνει ληστές σε στην τοποθεσία της ληστείας).'),
	(9, '1.8', 'Attempt - Απόπειρα', 'Οποιαδήποτε προσπάθεια επίτευξης εγκλήματος αποκαλείται "απόπειρα". Κάθε απόπειρα, τιμωρείται με ίσο χρόνο φυλάκισης και πρόστιμο με αυτό της κατηγορίας του κύριου εγκλήματος (πχ. Attempted Kidnapping = time/fine of Kidnapping)');
INSERT INTO `criminal_offenses` (`id`, `quote`, `category_id`, `legislation_id`, `offense`, `max_sentence`, `max_fine`, `appeal_trial`, `description`, `license_points`, `disabled`) VALUES
	(1, '100', 1, 4, 'Act of Torture (Πράξη βασανιστηρίων)', '120', '12000', 'trial', 'Η πράξη της σκόπιμης πρόκλησης ταλαιπωρίας ή σοβαρού τραυματισμού στη σωματική ή ψυχική υγεία ενός ατόμου. Αυτή, μπορεί να γίνει ώστε να αναγκαστεί κάποιος να κάνει ή να πει κάτι παρά τη δική του ελεύθερη βούληση.', 0, 0),
	(2, '101', 1, 3, '"Simple" Assault (Επίθεση)', '8', '600', 'appeal', 'Η παράνομη απόπειρα, σε συνδυασμό με την ικανότητα κάποιου, να διαπράξει έναν τραυματισμό έναντι άλλου.', 0, 0),
	(3, '102', 1, 3, 'Assault to Government Employee', '8', '600', 'appeal', 'Η παράνομη απόπειρα, σε συνδυασμό με την ικανότητα κάποιου, να διαπράξει έναν τραυματισμό έναντι κάποιου κυβερνητικού υπαλλήλου.', 0, 0),
	(4, '103', 1, 4, 'Assault With a Deadly Weapon (Επίθεση με θανατηφόρο όπλο)', '8', '800', 'appeal', 'Η παράνομη απόπειρα, σε συνδυασμό με την ικανότητα κάποιου, να διαπράξει έναν βίαιο τραυματισμό έναντι άλλου. Η πολιτεία του San Andreas, προβλέπει την παρούσα κατηγορία όταν κάποιος αποπειράται να προκαλέσει τραυματισμό χρησιμοποιώντας αιχμηρά αντικείμενα ή θανατηφόρα όπλα.', 0, 0),
	(5, '104', 1, 4, 'Battery (Βιαιοπραγία)', '16', '1200', 'appeal', 'Η εσκεμμένη και παράνομη χρήση βίας εναντίον την σωματικής ακεραιότητας κάποιου άλλου.', 0, 0),
	(6, '105', 1, 4, 'Battery to a Government Employee', '16', '1200', 'appeal', 'Η εσκεμμένη και παράνομη χρήση βίας εναντίον την σωματικής ακεραιότητας κάποιου κυβερνητικού υπαλλήλου.', 0, 0),
	(7, '106', 1, 3, 'Unlawful / False Imprisonment (Παράνομη φυλάκιση)', '8', '600', 'appeal', 'O περιορισμός της κίνησης ενός ατόμου σε οποιαδήποτε περιοχή χωρίς την συναίνεση του.', 0, 0),
	(8, '107', 1, 4, 'Attempted Kidnapping (Απόπειρα Απαγωγής)', '8', '800', 'appeal', 'Η απόπειρα μετακίνησης ενός ατόμου, σε σημαντική απόσταση, χωρίς τη συγκατάθεση του, με βία ή φόβο.', 0, 0),
	(9, '108', 1, 4, 'Kidnapping (Απαγωγή)', '16', '1200', 'appeal', 'Η μετακίνηση ενός ατόμου, σε σημαντική απόσταση, χωρίς τη συγκατάθεση του, με βία ή φόβο.', 0, 0),
	(10, '109', 1, 4, 'Kidnapping a Government Employee (Απαγωγή κυβερνητικού υπαλλήλου)', '16', '1200', 'appeal', 'Η μετακίνηση ενός κυβερνητικού υπαλλήλου, σε σημαντική απόσταση, χωρίς τη συγκατάθεση του, με βία ή φόβο. Οι ακόλουθοι τίτλοι χαρακτηρίζουν ένα άτομο ως κυβερνητικό υπάλληλο : (1) Αξιωματικοί Επιβολής του νόμου (LSPD,BCSO,SAPR,SASP), (2) Προσωπικό ιατρικών υπηρεσιών έκτακτης ανάγκης (EMS,FD), (3) Ιατροί (LSMC), (4) Προσωπικό του υπουργείου δικαιοσύνης (DOJ), (5) Προσωπικό του δημαρχείου της πολιτείας, (6) Υπάλληλοι της Κομητείας.', 0, 0),
	(11, '110', 1, 4, 'Involuntary Manslaughter (Ανθρωποκτονία εξ αμελείας)', '120', '12000', 'trial', 'Η συμπτωματική, εγκληματικά αμελής ή απερίσκεπτη, θανάτωση ενός ατόμου. Ειδικότερα η πολιτεία του San Andreas αναγνωρίζει ως Involuntary Manslaughter οποιοδήποτε φόνο διαπράττεται "κατά λάθος", δηλαδή όταν ο δράστης δεν έχει πρόθεση να σκοτώσει αλλά συμπτωματικά σκοτώνει κάποιον χωρίς να υπάρχει δόλος.', 0, 0),
	(12, '111', 1, 4, 'Voluntary Manslaughter (Εκούσια Ανθρωποκτονία)', '200', '24000', 'trial', 'Η παράνομη θανάτωση μιας ανθρώπινης οντότητας, η οποία είτε διαπράττεται εν βρασμώ ψυχής είτε αποτελεί ειλικρινή αλλά παράλογη αυτοάμυνα.', 0, 0),
	(13, '112', 1, 4, 'Attempted Second Degree Murder (Απόπειρα δολοφονίας 2ου βαθμού)', '24', '2000', 'trial', 'Η απόπειρα για την παράνομη θανάτωση ενός ατόμου με κακία εκ των προτέρων (Malice Aforethought) χωρίς πρόθεση ή προμελέτη. Ειδικότερα, η πολιτεία του San Andreas κατηγορεί για το παρόν έγκλημα οποιονδήποτε διαπράττει δολοφονία χωρίς να πληροί τα κριτήρια του First Degree Murder (ex. ένα άτομο πυροβολεί σε ένα γεμάτο δωμάτιο και τραυματίζει πολύ σοβαρά κάποιον, χωρίς ο δράστης να είχε σκοπό να το κάνει).', 0, 0),
	(14, '113', 1, 4, 'Second Degree Murder (Δολοφονία 2ου βαθμού)', '200', '24000', 'trial', 'Η παράνομη θανάτωση ενός ατόμου με κακία εκ των προτέρων (Malice Aforethought) χωρίς πρόθεση ή προμελέτη. Ειδικότερα, η πολιτεία του San Andreas κατηγορεί για το παρόν έγκλημα οποιονδήποτε διαπράττει δολοφονία χωρίς να πληροί τα κριτήρια του First Degree Murder (ex. ένα άτομο πυροβολεί σε ένα γεμάτο δωμάτιο και σκοτώνει κάποιον, χωρίς ο δράστης να είχε σκοπό να σκοτώσει).', 0, 0),
	(15, '114', 1, 4, 'Attempted First Degree Murder (Απόπειρα δολοφονίας 1ου βαθμού)', '24', '2000', 'appeal', 'Η απόπειρα για την παράνομη θανάτωση ενός ατόμου με κακία εκ των προτέρων (Malice Aforethought). Ειδικότερα, η πολιτεία του San Andreas κατηγορεί για το παρόν έγκλημα οποιονδήποτε πληροί έστω και ένα από τα ακόλουθα κριτήρια : (α) Αποπειράθηκε να διαπράξει δολοφονία χρησιμοποιώντας είτε έναν καταστροφικό μηχανισμό είτε εκρηκτικό είτε ένα όπλο μαζικής καταστροφής είτε πυρομαχικά υψηλής διαμέτρου είτε μέσω δηλητηρίασης , (β) Αποπειράθηκε να διαπράξει δολοφονία με το να περιμένει το θύμα , (γ) Αποπειράθηκε να διαπράξει δολοφονία προκαλώντας βασανιστήρια , (δ) Αποπειράθηκε να διαπράξει δολοφονία εκούσια ή με βάση προμελετημένου σχεδίου (δηλαδή, η δολοφονία κάποιου έπειτα από συνεχόμενα κακουργήματα).', 0, 0),
	(16, '115', 1, 4, 'First Degree Murder (Δολοφονία 1ου βαθμού)', '280', '48000', 'trial', 'Η παράνομη θανάτωση ενός ατόμου με κακία εκ των προτέρων (Malice Aforethought) και προμελέτη. Ειδικότερα, η πολιτεία του San Andreas κατηγορεί για το παρόν έγκλημα οποιονδήποτε πληροί τουλάχιστον ένα από τα ακόλουθα κριτήρια : (α) Διέπραξε δολοφονία χρησιμοποιώντας είτε έναν καταστροφικό/εκρηκτικό μηχανισμό είτε ένα όπλο μαζικής καταστροφής είτε πυρομαχικά υψηλής διαμέτρου είτε μέσω δηλητηρίασης, (β) Διέπραξε δολοφονία περιμένοντας για ώρα το θύμα, (γ) Διέπραξε δολοφονία προκαλώντας βασανιστήρια, (δ) Διέπραξε δολοφονία εκούσια ή βάση προμελετημένου σχεδίου (δηλαδή, η δολοφονία κάποιου έπειτα από συνεχόμενα κακουργήματα).', 0, 0),
	(17, '116', 1, 4, 'Attempted Murder of a Government Employee (Απόπειρα δολοφονίας Κυβερνητικού Υπαλλήλου)', '16', '1200', 'appeal', 'Η απόπειρα για την παράνομη θανάτωση ενός κυβερνητικού υπαλλήλου με κακία εκ των προτέρων (Malice Aforethought). Ειδικότερα, η πολιτεία του San Andreas κατηγορεί για το παρόν έγκλημα οποιονδήποτε πληροί έστω και ένα από τα ακόλουθα κριτήρια : (α) Αποπειράθηκε να διαπράξει δολοφονία χρησιμοποιώντας είτε έναν καταστροφικό μηχανισμό είτε εκρηκτικό είτε ένα όπλο μαζικής καταστροφής είτε πυρομαχικά υψηλής διαμέτρου είτε μέσω δηλητηρίασης , (β) Αποπειράθηκε να διαπράξει δολοφονία με το να περιμένει το θύμα , (γ) Αποπειράθηκε να διαπράξει δολοφονία προκαλώντας βασανιστήρια , (δ) Αποπειράθηκε να διαπράξει δολοφονία εκούσια ή με βάση προμελετημένου σχεδίου (δηλαδή, η απόπειρα δολοφονίας κάποιου έπειτα από συνεχόμενα κακουργήματα).', 0, 0),
	(18, '117', 1, 3, 'Criminal Threats (Εγκληματικές απειλές)', '4', '400', 'appeal', 'Η δήλωση/άρθρωση/εκφώνηση εκφράσεων, αναφορών ή σχολίων με τα οποία υποδηλώνεται μια ειδεχθής εγκληματική πράξη εις βάρος κάποιου ατόμου. Οι εγκληματικές απειλές αφορούν εγκλήματα τα οποία αναφέρονται σε σωματική βλάβη ή θάνατο. Το θύμα τους, βρίσκεται σε κατάσταση εύλογου παρατεταμένου φόβου για την ασφάλειά του ή για την ασφάλεια της οικογένειάς του και οι απειλές είναι συγκεκριμένες και ξεκάθαρες.', 0, 0),
	(19, '118', 1, 4, 'Gang Related Shooting (Ένοπλη συμπλοκή συμμοριών)', '32', '3200', 'appeal', 'Κάθε άτομο που σχετίζεται με συμμορία που εμπλέκεται σε πυροβολισμούς/πυροβολισμούς με μέλη άλλης συμμορίας. Ένα άτομο είναι συνδεδεμένο με συμμορίες για τους σκοπούς αυτής της κατηγορίας όταν συμμετέχει ενεργά ή προωθεί σκόπιμα τους σκοπούς μιας εγκληματικής συμμορίας του δρόμου. Αυτό μπορεί να διαπιστωθεί είτε από: (1) ένα πρότυπο συσχέτισης και συμμετοχής σε κακουργηματική δραστηριότητα, είτε (2) από δηλώσεις / παραδοχές σύνδεσης ή/και συμμετοχής που γίνονται από το άτομο. Ως "εγκληματική συμμορία του δρόμου" ορίζεται μια συνεχιζόμενη ομάδα, λέσχη ή οργάνωση που έχει πρωταρχικό σκοπό τη διάπραξη ποινικών αδικημάτων ή τα μέλη της οποίας τις τελευταίες εξήντα ημέρες έχουν εμπλακεί σε μια συνεχόμενη ή συνεχιζόμενη σειρά κακουργημάτων .', 0, 0),
	(20, '119', 1, 4, 'Reckless Endangerment (Πρόκληση αλόγιστου κινδύνου)', '8', '800', 'appeal', 'Η συμπεριφορά ή οι αμελείς πράξεις οι οποίες θέτουν την ζωή ενός ατόμου σε άμεσο κίνδυνο, συσχετιζόμενο με σοβαρό σωματικό τραυματισμό. Ο θύτης, παρουσιάζει αδιαφορία για τις εύλογες συνέπειες των πράξεών του.', 0, 0),
	(21, '120', 1, 4, 'Forcer\'s Law (Serial Killings)', '280', '48000', 'trial', 'Η πολιτεία του San Andreas θα θεωρήσει κάθε δράστη που διαπράττει το κακούργημα της δολοφονίας πρώτου βαθμού σε τουλάχιστον τρεις ξεχωριστές χρονικές στιγμές ως κατά συρροή δολοφόνο και θα αποδώσει την θανατική ποινή.', 0, 0),
	(22, '200', 2, 3, 'Petty Theft (Μικροκλοπή)', '4', '400', 'appeal', 'Η κλοπή περιουσίας, χρηματικής αξίας μικρότερης ή ίσης των 950$ από άλλο άτομο.', 0, 0),
	(23, '201', 2, 2, 'Grand Theft (Μεγαλοκλοπή)', '8', '600', 'appeal', 'Η κλοπή περιουσίας, χρηματικής αξίας μεγαλύτερης των 950$ από άλλο άτομο.', 0, 0),
	(24, '202', 2, 4, 'Grand Larceny (Μεγαλοκλοπή)', '32', '3200', 'trial', 'Η σκόπιμη κλοπή περιουσίας ενός ατόμου, καταστήματος, επιχείρησης, χρηματοπιστωτικού ιδρύματος ή οργανισμού η οποία εκτιμάται με χρηματική αξία ίση ή μεγαλύτερη των 25,000$, χωρίς την συγκατάθεσή του/τους. Η πολιτεία, αναγνωρίζει ως χρηματοπιστωτικά ιδρύματα τα εξής : (1) Κάθε δημόσια ή ιδιωτική τράπεζα (etc. Fleeca Banks), (2) Diamond Hand Casino, (3) Οποιoδήποτε ίδρυμα το οποίο ελέγχει και επιμελείται περιουσιακά στοιχεία του κράτους ή ιδιωτών.', 0, 0),
	(25, '203', 2, 3, 'Joyriding (Κλοπή οχήματος)', '8', '600', 'appeal', 'Η απόσπαση ενός οχήματος χωρίς την συγκατάθεση του νόμιμου ιδιοκτήτη του με σκοπό την βραχυχρόνια (προσωρινή) χρήση του.', 0, 0),
	(26, '204', 2, 4, 'Felony Joyriding (Κακουργηματική Κλοπή οχήματος)', '16', '1200', 'appeal', 'Η απόσπαση ενός κυβερνητικού οχήματος χωρίς την συγκατάθεση της αρμόδιας δημόσιας υπηρεσίας με σκοπό την βραχυχρόνια (προσωρινή) χρήση του. Η πολιτεία του San Andreas αναγνωρίζει ως κυβερνητικά οχήματα τα εξής : (α) Οχήματα που φέρουν διακριτικά υπηρεσίας επιβολής του νόμου όπως LSPD, BCSO, SASPR, SASP, CID, (β) Οχήματα ιατρικής περίθαλψης και άμεσης βοήθειας του EMS, LSMC και LSFD.', 0, 0),
	(27, '205', 2, 4, 'Carjacking (Ληστεία Οχήματος)', '16', '1200', 'appeal', 'H απόσπαση/αφαίρεση/κλοπή ενός μηχανοκίνητου οχήματος από τον ιδιοκτήτη/χειριστή/συνεπιβάτη του, η οποία διαπράττεται ενώ αυτός είναι παρόν και δεν συναινεί σε αυτή, με την χρήση βίας ή απειλών για τραυματισμό και αποσκοπεί στο να το στερήσει από αυτόν για μικρό ή μεγάλο χρονικό διάστημα.', 0, 0),
	(28, '206', 2, 4, 'Grand Theft Auto (GTA)', '16', '1200', 'appeal', 'Η κλοπή κάποιου οχήματος, αξίας 950 $ ή περισσότερο, χωρίς άδεια τον νόμιμο ιδιοκτήτη του, με σκοπό να στερηθεί από αυτόν. Η πολιτεία του San Andreas, αποδίδει την παρούσα κατηγορία σε οποιονδήποτε πράττει έστω και μία από τις παρακάτω ενέργειες : Η κλοπή αυτοκινήτου για χρήση ως όχημα απόδρασης από άλλο έγκλημα ή/και η κλοπή ενός αυτοκινήτου με σκοπό την πώλησή του ή τον διαμερισμό του για πώληση των ανταλλακτικών του.', 0, 0),
	(29, '207', 2, 3, 'Tampering with a Vehicle (Παραβίαση οχήματος)', '4', '400', 'appeal', 'Η πράξη με την οποία ένα άτομο σκοπίμως παραβιάζει, φθείρει, σπάει ολικά ένα μηχανοκίνητο όχημα ή αφαιρεί μεμονομένα μέρη του, χωρίς αυτό να του ανήκει ή να έχει λάβει την συγκατάθεση του νόμιμου ιδιοκτήτη του.', 0, 0),
	(30, '208', 2, 4, 'Attempted Robbery (Απόπειρα Ληστείας)', '8', '800', 'appeal', 'Η απόπειρα για κακουργηματική αφαίρεση προσωπικής περιουσίας, από το πρόσωπο ή την άμεση παρουσία του και παρά τη θέλησή του, η οποία πραγματοποιείται με βία ή φόβο.', 0, 0),
	(31, '209', 2, 4, 'Robbery (Ληστεία)', '16', '1200', 'appeal', 'Η κακουργηματική αφαίρεση προσωπικής περιουσίας, από το πρόσωπο ή την άμεση παρουσία του και παρά τη θέλησή του, η οποία πραγματοποιείται με βία ή φόβο.', 0, 0),
	(32, '210', 2, 4, 'Robbery of a Financial Institution (Ληστεία χρηματοπιστωτικού ιδρύματος)', '24', '2000', 'appeal', 'Η κακουργηματική αφαίρεση/κλοπή και παράνομη απόσπαση χρηματικών κεφαλαίων τα οποία επηρεάζουν άμεσα την σταθερότητα του οικονομικού συστήματος της πολιτείας του San Andreas είτε τα χρηματοπιστωτικά ιδρύματα ως καθαυτά. Η πολιτεία, αναγνωρίζει ως χρηματοπιστωτικά ιδρύματα τα εξής : (1) Κάθε δημόσια ή ιδιωτική τράπεζα (etc. Fleeca Banks), (2) Οποιoδήποτε ίδρυμα το οποίο ελέγχει και επιμελείται περιουσιακά στοιχεία του κράτους ή ιδιωτών.', 0, 0),
	(33, '211', 2, 3, 'Misdemeanor Possession of Marked Bills', '4', '400', 'appeal', 'Η παράνομη κατοχή αφορολόγητων χρημάτων (Marked Bills), εώς και τρεις τσάντες, των οποίων η προέλευση δεν δικαιολογείται από το οικονομικό υπόβαθρο του κατόχου.', 0, 0),
